import React, { useEffect, useState } from 'react';
import { Badge, Calendar, message, Modal } from 'antd';
import axios from 'axios';
import moment from 'moment';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faFlag } from '@fortawesome/free-solid-svg-icons';

const colors = ['pink', 'green', 'purple', 'blue', 'red', 'yellow', 'orange', 'cyan', 'geekblue', 'magenta', 'volcano', 'gold', 'lime'];

const TaskCalendar = ({ apiUrl, userData }) => {
  const [todos, setTodos] = useState([]);
  const [modalVisible, setModalVisible] = useState(false);
  const [modalContent, setModalContent] = useState([]);

  useEffect(() => {
    fetchDataAndFilterTasks();
  }, []);

  const fetchDataAndFilterTasks = async () => {
    try {
      const response = await axios.get(apiUrl);
      const allData = response.data.data;

      const filteredUsers = allData.filter(item => item.UserID === userData.UserID);
      const tasksWithDates = filteredUsers.map(user => {
        const [startDate, endDate] = user.Date.split(' to ').map(date => moment(date, 'DD-MM-YYYY').format('YYYY-MM-DD'));
        const subtasks = user.SubTask ? user.SubTask.split(',').map(subtask => ({
          name: subtask.trim(),
          dateRange: user.SubTaskDateRanges.split(',').map(date => moment(date.trim(), 'DD-MM-YYYY').format('YYYY-MM-DD'))
        })) : [];
        return {
          task: user.Task,
          startDate,
          endDate,
          subtasks
        };
      });

      setTodos(tasksWithDates);

    } catch (error) {
      console.error('Failed to fetch data:', error);
      message.error(`Veriler alınamadı. Lütfen daha sonra tekrar deneyin. Hata: ${error.message}`);
    }
  };

  // Function to group tasks and subtasks by date
  const groupTasksByDate = () => {
    const groupedTasks = {};

    todos.forEach(task => {
      // Push main task
      for (let date = moment(task.startDate); date.isSameOrBefore(task.endDate, 'day'); date.add(1, 'day')) {
        const formattedDate = date.format('YYYY-MM-DD');
        if (!groupedTasks[formattedDate]) {
          groupedTasks[formattedDate] = [];
        }
        groupedTasks[formattedDate].push({
          ...task,
          isSubtask: false
        });
      }

      // Push subtasks
      task.subtasks.forEach(subtask => {
        subtask.dateRange.forEach(subtaskDate => {
          const formattedSubtaskDate = moment(subtaskDate).format('YYYY-MM-DD');
          if (!groupedTasks[formattedSubtaskDate]) {
            groupedTasks[formattedSubtaskDate] = [];
          }
          groupedTasks[formattedSubtaskDate].push({
            task: subtask.name,
            startDate: subtaskDate,
            endDate: subtaskDate,
            isSubtask: true
          });
        });
      });
    });

    return groupedTasks;
  };

  // Render tasks on calendar
  const renderTasksOnCalendar = value => {
    const groupedTasks = groupTasksByDate();
    const date = value.format('YYYY-MM-DD');
    const tasksForDate = groupedTasks[date] || [];

    return (
      <ul style={{ padding: 0 }}>
        {tasksForDate.map((task, index) => (
          <li key={index} style={{ marginBottom: 8 }}>
            {task.isSubtask ? (
              <Badge color={colors[index % colors.length]} text={task.task} />
            ) : (
              <>
                <Badge color={colors[index % colors.length]} text={task.task} />
                {moment(task.startDate).isSame(moment(date), 'day') && (
                  <span style={{ marginLeft: 8, color: 'rgba(0, 0, 0, 0.45)' }}>
                    <FontAwesomeIcon icon={faPlay} />
                  </span>
                )}
                {moment(task.endDate).isSame(moment(date), 'day') && (
                  <span style={{ marginLeft: 8, color: 'rgba(0, 0, 0, 0.45)' }}>
                    <FontAwesomeIcon icon={faFlag} />
                  </span>
                )}
              </>
            )}
          </li>
        ))}
      </ul>
    );
  };

  // Handle date cell click to show modal with tasks for that date
  const handleDateClick = value => {
    const date = value.format('YYYY-MM-DD');
    const tasksForDate = groupTasksByDate()[date] || [];
    setModalContent(tasksForDate);
    setModalVisible(true);
  };

  // Modal close handler
  const handleModalClose = () => {
    setModalVisible(false);
  };

  return (
    <div>
      <Calendar
        dateCellRender={renderTasksOnCalendar}
        onSelect={handleDateClick}
      />
      <Modal
        title="Görevler"
        visible={modalVisible}
        onCancel={handleModalClose}
        footer={null}
      >
        {modalContent && Array.isArray(modalContent) && modalContent.length > 0 ? (
          modalContent.map((task, index) => (
            <div key={index} style={{ marginBottom: 16 }}>
              <h3>{task.task}</h3>
              {task.isSubtask && (
                <p>Alt Görev: {task.task}</p>
              )}
              <p>Tarih: {moment(task.startDate).format('DD-MM-YYYY')}{moment(task.startDate).format('DD-MM-YYYY')}</p>
            </div>
          ))
        ) : (
          <p>Henüz görev bulunmamaktadır.</p>
        )}
      </Modal>
    </div>
  );
};

export default TaskCalendar;
