import { useRef, useState, useEffect } from 'react';
import { message } from 'antd';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Draw from '../common/drawer';

const LoginData = () => {
    const navigate = useNavigate();
    const formRef = useRef(null);
    const [data, setData] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [loggedInUser, setLoggedInUser] = useState(null); // State to hold logged-in user information
    const apiUrl = 'https://v1.nocodeapi.com/gizem11/google_sheets/ICsbCoIOoLbnWrHj?tabId=login';

    const handleLogin = async (values) => {
        const { Email, myPassword } = values;

        // Check if the user exists in the fetched data
        const user = data.find(item => item.Email === Email && item.Password === myPassword);

        if (user) {
            message.success('Successful login'); // Successful login message
            setIsLoading(true); // Set loading state to true
            setLoggedInUser(user);
            // Simulated redirection (after 1.1 seconds)
            setTimeout(() => {
                setIsLoading(false); // Set loading state to false
                 // Set logged-in user information
                navigate('/home'); // Redirect to /home page
            }, 1100);
            setTimeout(() => {
                message.destroy(); // Destroy current message
            }, 500);
        } else {
            message.error('Incorrect email or password'); // Incorrect email or password message
        }
    };

    const getData = async () => {
        try {
            const response = await axios.get(apiUrl);
            setData(response.data.data);
        } catch (error) {
            console.error('Failed to fetch data', error);
        }
    };

    useEffect(() => {
        getData(); // Fetch data
    }, []);

    return (
        <Draw loggedInUser></Draw>
    ); // Return loggedInUser as part of the object

};

export default LoginData;
