import React, { useState } from "react";
import { Form, Input, Button, message } from 'antd';

const TaskForm = ({ addTask }) => {
  const [value, setValue] = useState('');

  const handleSubmit = () => {
    if (value) {
      addTask(value);
      setValue('');
    } else {
      message.warning('Lütfen bir görev girin!');
    }
  };

  return (
    <Form onFinish={handleSubmit} layout="inline">
      <Form.Item
        name="taskInput"
        style={{ flex: 1 }}
        rules={[{ required: true, message: 'Lütfen bir görev girin!' }]}
      >
        <Input
          placeholder="Görev Ekleyin"
          size="large"
          value={value}
          onChange={(e) => setValue(e.target.value)}
        />
      </Form.Item>
      <Form.Item>
        <Button
          htmlType="submit"
          type="primary"
          size="large"
        >
          Ekle
        </Button>
      </Form.Item>
    </Form>
  );
};

export default TaskForm;
