import React from 'react';
import { useLocation } from 'react-router-dom';
const Task = () => {
  const { state } = useLocation();
  const userData = state?.userData;
  const tasks = userData.Task.split(',');

  return (
    <div style={{ marginLeft: '600px', padding: '20px' }}>
      <h2>Tasks:</h2>
      <ul>
        {tasks.map((task, index) => (
          <li key={index}>{task.trim()}</li> // Her görevi bir liste öğesi olarak göster, trim() ile boşlukları temizle
        ))}
      </ul>
    </div>
  );
};

export default Task;
