import { useRef, useState, useEffect } from 'react';
import { message } from 'antd';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Draw from '../common/drawer';

const LoginData = () => {
    const navigate = useNavigate();
    const formRef = useRef(null);
    const [data, setData] = useState([]);
    const [userData, setUserData] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const apiUrl = 'https://v1.nocodeapi.com/gizem11/google_sheets/ICsbCoIOoLbnWrHj?tabId=login';

    const handleLogin = async (values) => {
        const { Email, myPassword } = values;

        // Check if the user exists in the fetched data
        const user = data.find(item => item.Email === Email && item.Password === myPassword);

        if (user) {
            message.success('Successful login'); // Successful login message
            setIsLoading(true);
            setUserData(user); // Set loading state to true
            const userName = userData.User; // Assuming 'Name' is the property containing user's name
            const userId = userData.UserID; // Assuming 'id' is the property containing user's ID

            console.log(`Logged in user: ${userName}, ID: ${userId}`);

            // Simulated redirection (after 1.1 seconds)
            setTimeout(() => {
                setIsLoading(false); // Set loading state to false

                navigate('/home'); // Redirect to /home page
            }, 1100);
            setTimeout(() => {
                message.destroy(); // Destroy current message
            }, 500);
        } else {
            message.error('Incorrect email or password'); // Incorrect email or password message
        }
    };

    const getData = async () => {
        try {
            const response = await axios.get(apiUrl);
            setData(response.data.data);
        } catch (error) {
            console.error('Failed to fetch data', error);
        }
    };

    useEffect(() => {
        getData(); // Fetch data
    }, []);

    return { formRef, handleLogin, isLoading,userData}; // Return loggedInUser as part of the object

};

export default LoginData;
