import React, { useState } from 'react';
import { Form, Input, Button } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';

const TaskForm = ({ userData }) => {
    const [name, setName] = useState('');
    const [userExists, setUserExists] = useState(true);

    const handleChange = (e) => {
        setName(e.target.value);
    };

    const handleSubmit = async () => {
        try {
            // Kullanıcıyı kontrol et
            const userResponse = await axios.get(
                'https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm?tabId=login',
                {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                }
            );
            const users = userResponse.data.data;
            const userIndex = users.findIndex(user => user.UserID === userData.UserID);

            if (userIndex === -1) {
                setUserExists(false);
                console.error('Kullanıcı mevcut değil!');
                return;
            }

            // Kullanıcı mevcutsa görevi ekle
            const userRow = users[userIndex];
            const updatedTask = userRow.Task ? `${userRow.Task}, ${name}` : name; // Var olan göreve yeni görevi ekle

            users[userIndex] = {
                ...userRow,
                Task: updatedTask
            };

            await axios.put(
                'https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm?tabId=login',
                {
                    data: users
                },
                {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                }
            );

            console.log('Görev başarıyla güncellendi:', updatedTask);
        } catch (error) {
            if (error.response) {
                console.error('Hata oluştu:', error.response.data);
            } else {
                console.error('Hata oluştu:', error.message);
            }
        }
    };

    return (
        <div>
            <Form onFinish={handleSubmit}>
                <Form.Item
                    name="taskInput"
                    rules={[{ required: true, message: 'Lütfen bir görev girin!' }]}
                >
                    <Input
                        name='görev'
                        placeholder="Görev Ekleyin"
                        value={name}
                        onChange={handleChange}
                        size="large"
                    />
                </Form.Item>
                {!userExists && <div style={{ color: 'red' }}>Kullanıcı mevcut değil!</div>}
                <Form.Item>
                    <Button
                        htmlType="submit"
                        type="primary"
                        icon={<FileAddOutlined />}
                        size="large"
                    >
                        Ekle
                    </Button>
                </Form.Item>
            </Form>
        </div>
    );
};

export default TaskForm;
