import React from 'react';
import { Typography, Form, Input, Button, message, ConfigProvider, Row, Col } from 'antd';
import "./login.css";

const Login = () => {

    const handleLogin = (values) => {
        message.success("Başarılı");
    }

    return (
        <ConfigProvider
            theme={{
                token: {
                    colorPrimary: '#d11ba7',
                    colorPrimaryHover: '#5b10b5',
                    colorText: "#fff",
                },
            }}
        >
            <div className="appBg">
                <Row justify="center" align="middle" style={{ minHeight: '100vh' }}>
                    <Col xs={24} sm={20} md={16} lg={12} xl={10}>
                        <Form className="loginForm" onFinish={handleLogin}>
                            <Typography.Title style={{ textAlign: 'center' }}>Hoş Geldiniz</Typography.Title>

                            <Row justify="center">
                                <Col span={24}>
                                    <Form.Item
                                        label="Email"
                                        name="myEmail"
                                        rules={[
                                            {
                                                required: true,
                                                type: "email",
                                                message: "Lütfen geçerli bir email adresi girin."
                                            }
                                        ]}
                                    >
                                        <Input style={{ color: "black" }} placeholder="E-mail girin" />
                                    </Form.Item>
                                </Col>
                            </Row>

                            <Row justify="center">
                                <Col span={24}>
                                    <Form.Item
                                        label="Şifreeee"
                                        name="myPassword"
                                        rules={[
                                            {
                                                required: true,
                                                message: "Lütfen şifrenizi girin."
                                            }
                                        ]}
                                    >
                                        <Input.Password style={{ color: "black" }} placeholder="Şifre girin" />
                                    </Form.Item>
                                </Col>
                            </Row>

                            <Form.Item style={{ textAlign: 'center' }}>
                                <Button type='primary' htmlType='submit' block>Giriş Yap</Button>
                            </Form.Item>
                        </Form>
                    </Col>
                </Row>
            </div>
        </ConfigProvider>
    );
}

export default Login;
