import React,{useState} from 'react';
import { Button, message, Popconfirm } from 'antd';
import axios from 'axios';

const Tasks = ({userData}) => {
    const [tasks, setTasks] = useState([]);
    const apiUrl = 'https://v1.nocodeapi.com/gizem004/google_sheets/rmLuKVNiJbmqpjdA?tabId=login';
    const fetchData = async () => {
        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;

            const filteredUsers = allData.filter(item => item.UserID === userData.UserID);
            const tasks = filteredUsers.flatMap(user => user.Task.split(',').map(task => task.trim()));
            setTasks(tasks);

        } catch (error) {
            console.error('Failed to fetch data', error);
            message.error('Veriler alınamadı. Lütfen daha sonra tekrar deneyin.');
        }
    };

    return (
        <>
        fetchData()
        <div style={{ padding: '20px' }}>
            <h2>Görevleriniz:</h2>
            {tasks && tasks.length > 0 ? (
                tasks.map((task, index) => (
                    <div key={index} style={{ marginBottom: '10px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <Button style={{ flex: 1, marginRight: '10px' }} block>
                            {task}
                        </Button>

                    </div>
                ))
            ) : (
                <p>Yükleniyor...</p>
            )}
        </div>
        </>
    );
};

export default Tasks;
