import React, { useState } from 'react';
import { Drawer, Button, Space, Avatar } from 'antd';
import { PoweroffOutlined, UserOutlined } from '@ant-design/icons';
import Task from '../components/task';



const Draw = ({ userData, itemsList }) => {
  const [selectedItem, setSelectedItem] = useState(null);

  const handleItemClick = (item) => {
    setSelectedItem(item.key);
  };

  const handleLogout = () => {
    console.log("Çıkış yapıldı");
    // Çıkış işlemi veya başka bir işlem yapılabilir
  };

  // Seçilen öğeye göre hangi bileşeni göstereceğimizi belirleyen fonksiyon
  const renderComponent = () => {
    switch (selectedItem) {
      case 'task':
        return <Task userData={userData} />;

      default:
        return null;
    }
  };

  return (
    <>
      <Drawer
        title={
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <Avatar size="large" icon={<UserOutlined />} />
            <span style={{ marginLeft: '10px', fontSize: '20px', fontWeight: 'bold' }}>
              {userData.User}
            </span>
          </div>
        }
        placement="left"
        closable={false}
        onClose={() => {}} // Kapatma işlemi olmadığı için boş fonksiyon
        visible={true} // Drawer'ı her zaman açık tut
      >
        <Space direction="vertical" style={{ width: '100%' }} align='start'>
          {itemsList.map((item) => (
            <Button
              key={item.key}
              type="text"
              icon={item.icon}
              style={{
                width: '100%',
                textAlign: 'left',
                fontSize: '16px',
              }}
              onClick={() => handleItemClick(item)}
            >
              {item.label}
            </Button>
          ))}
          <Button
            key="logout"
            type="text"
            icon={<PoweroffOutlined />}
            style={{ width: '100%', textAlign: 'left', fontSize: '16px' }}
            onClick={handleLogout}
          >
            Çıkış
          </Button>
        </Space>
      </Drawer>
      <div style={{ marginLeft: '400px', padding: '20px' }}>
        {renderComponent()}
      </div>
    </>
  );
};

export default Draw;
