import React,{useRef}from 'react';
import { Typography, Form, Input, Button, message, ConfigProvider } from 'antd';
import "./login.css";
import { UserOutlined } from '@ant-design/icons';
const Login = () => {
    const formRef = useRef(null);
    const handleLogin = (values) => {
        message.success("Başarılı");
        formRef.current.resetFields();

    }

    return (
        <ConfigProvider
        theme={{
            token: {
                colorPrimary: '#d11ba7',
                colorPrimaryHover: '#5b10b5'},

            components:{

                Input:{


                    colorText: "#black",
                },

            },

        }}

        >
            <div className="appBg">
                <Form className="loginForm" onFinish={handleLogin} ref={formRef}>
                    <Typography.Title style={{ textAlign: 'center',color:"#fff"}}>Hoş Geldiniz</Typography.Title>

                    <Form.Item
                        name="Email"
                        rules={[
                            {
                                required: true,
                                type: "email",
                                message: "Lütfen geçerli bir email adresi girin."
                            }
                        ]}
                    >
                        <Input placeholder="Email" suffix={<UserOutlined />} size='large' style={{ border: ""}} />
                    </Form.Item>

                    <Form.Item
                        name="myPassword"
                        rules={[
                            {
                                required: true,
                                message: "Lütfen şifrenizi girin."
                            }
                        ]}
                    >
                        <Input.Password placeholder="Şifre" size='large' />
                    </Form.Item>

                    <Form.Item style={{ textAlign: 'center' }}>
                        <Button type='primary' htmlType='submit'  block size='large'>Giriş Yap</Button>
                    </Form.Item>


                </Form>
            </div>
            </ConfigProvider>
    );
}

export default Login;
