import React from 'react';
import { Typography, Form, Input, Button, message, ConfigProvider } from 'antd';
import "./login.css";

const Login = () => {

    const handleLogin = (values) => {
        message.success("Başarılı");

    }

    return (
        <ConfigProvider
            theme={{
                token: {
                    colorPrimary: '#5b10b5',
                    colorPrimaryHover: '#d11ba7', // Örnek bir renk tokenı
                    colorText: "#fff",
                    //colorBgMask:"#d11ba7"
                },


            }}
        >
            <div className="appBg">
                <Form className="loginForm" onFinish={handleLogin}>
                    <Typography.Title style={{ textAlign: 'center' }}>Hoş Geldiniz</Typography.Title>

                    <Form.Item

                        name="myEmail"
                        rules={[
                            {
                                required: true,
                                type: "email",
                                message: "Lütfen geçerli bir email adresi girin."
                            }
                        ]}
                    >
                        <Input style={{color:"black"}}  placeholder="E-mail girin" />
                    </Form.Item>
                    <Form.Item

                        name="myPassword"
                        rules={[
                            {
                                required: true,
                                message: "Lütfen şifrenizi girin."
                            }
                        ]}
                    >
                        <Input placeholder="Şifre girin" />
                    </Form.Item>
                    <Form.Item style={{ textAlign: 'center' }}>
                        <Button type='primary' htmlType='submit' style={{width:40}} block size='large'>Giriş Yap</Button>
                    </Form.Item>


                </Form>
            </div>
        </ConfigProvider>
    );
}

export default Login;
