import React, { useState, useEffect, useRef } from 'react';
import { Form, Input, Button, message } from 'antd';
import axios from 'axios';
import TaskAddList from './taskAddList';

const TaskAdd = ({ userData }) => {
    const [name, setName] = useState('');
    const [data, setData] = useState([]); // State to hold all tasks
    const formRef = useRef(null);
    const apiUrl = 'https://v1.nocodeapi.com/gizem004/google_sheets/rmLuKVNiJbmqpjdA?tabId=login';

    // Function to fetch and filter tasks
    const fetchDataAndFilterTasks = async () => {
        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;

            const filteredUsers = allData.filter(item => item.UserID === userData.UserID);
            const tasks = filteredUsers.flatMap(user => user.Task.split(',').map(task => task.trim()));
            setData(tasks); // Set initial data

        } catch (error) {
            console.error('Failed to fetch data', error);
            message.error('Veriler alınamadı. Lütfen daha sonra tekrar deneyin.');
        }
    };

    // Fetch initial data on component mount
    useEffect(() => {
        fetchDataAndFilterTasks();
    }, []);

    // Handle input change
    const handleChange = (e) => {
        setName(e.target.value);
    };

    // Handle form submission
    const handleSubmit = async (values) => {
        try {
            const taskData = [
                [
                    userData.Email,
                    userData.Password,
                    userData.User,
                    name,
                    userData.UserID
                ]
            ];

            // Post new task data
            await axios.post(apiUrl, taskData);

            // Clear form field and notify success
            formRef.current.resetFields();
            setName('');
            message.success('Görev başarıyla eklendi.');

            // Update state to reflect new task
            setData(prevData => [...prevData, name]);

        } catch (error) {
            message.error('Görev eklenirken bir hata oluştu. Lütfen tekrar deneyin.');
            console.error('Görev eklenirken hata oluştu:', error);
        }
    };

    return (
        <div>
            <Form onFinish={handleSubmit} ref={formRef} layout="inline">
                <Form.Item name="taskInput" style={{ flex: 1 }}>
                    <Input
                        name="görev"
                        placeholder="Görev Ekleyin"
                        value={name}
                        onChange={handleChange}
                        size="large"
                    />
                </Form.Item>
                <Form.Item>
                    <Button
                        htmlType="submit"
                        type="primary"
                        size="large"
                        // Disable button if input is empty or only whitespace
                    >
                        Ekle
                    </Button>
                </Form.Item>
            </Form>
            <TaskAddList data={data}/>
        </div>
    );
};

export default TaskAdd;
