import React, { useState } from 'react';
import { Drawer, Button, Space, Avatar } from 'antd';
import { PoweroffOutlined, UserOutlined } from '@ant-design/icons';
import { MailOutlined, SettingOutlined, FileTextOutlined, CalendarOutlined, FileAddOutlined } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import Task from '../components/task'; // Task bileşenini ekledik

const itemsList = [
  {
    label: "Görevler",
    key: 'task',
    icon: <FileTextOutlined />
  },
  {
    label: "Görev Ekle",
    key: 'task2',
    icon: <FileAddOutlined />
  },
  {
    label: "Takvim",
    key: 'calendar',
    icon: <CalendarOutlined />
  },
  {
    label: 'Mail',
    key: 'mail',
    icon: <MailOutlined />,
  },
  {
    label: 'Profil Düzenle',
    key: 'setting:1',
    icon: <SettingOutlined />,
  }
];

const Draw = ({ userData }) => {
  const navigate = useNavigate();
  const [selectedMenuItem, setSelectedMenuItem] = useState(null); // Seçili menü öğesini takip etmek için state ekledik

  const onLogout = () => {
    navigate(-1); // Navigate to previous page or perform logout action
  };

  const onItemClick = (key) => {
    // Menü öğesine tıklandığında seçili menü öğesini güncelle
    setSelectedMenuItem(key);
  };

  return (
    <Drawer
      title={
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <Avatar size="large" icon={<UserOutlined />} /> {/* Display user's profile picture */}
          <span style={{ marginLeft: '10px', fontSize: '20px', fontWeight: 'bold' }}>
            {userData.User}
          </span>
        </div>
      }
      placement="left"
      closable={false}
      mask={false}
      onClose={() => { }} // No-op function to prevent closing on ESC key
      visible={true} // Make sure this is controlled based on your application's state
    >
      <Space direction="vertical" style={{ width: '100%' }}>
        {itemsList.map((item) => (
          <Button
            key={item.key}
            type="text"
            icon={item.icon}
            style={{ width: '100%', textAlign: 'left', fontSize: '16px' }}
            onClick={() => onItemClick(item.key)}
          >
            {item.label}
          </Button>
        ))}

        <Button
          key="logout"
          type="primary"
          icon={<PoweroffOutlined />}
          style={{ width: '100%', marginTop: 'auto', fontSize: '18px' }}
          onClick={onLogout}
        >
          Çıkış
        </Button>
      </Space>

      {/* Seçili menü öğesine göre Task bileşenini göster */}
      {selectedMenuItem === 'task' && <Task userData={userData} />}
    </Drawer>
  );
};

export default Draw;
