import React, { useState } from 'react';
import { Button, Menu, Avatar } from 'antd';
import { UserOutlined, MailOutlined, SettingOutlined, PoweroffOutlined } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';

const { SubMenu } = Menu;

const items = [

  {
    key: 'app',
    avatar: <Avatar size={20} icon={<UserOutlined />} />,
    children: [
      {
        label: 'Profil Düzenle',
        key: 'setting:1',
      },
      {
        label: 'Mail yolla',
        icon: <MailOutlined />,
        key: 'setting:2',
      },
      {
        icon
      }
    ],
  },
];

const Home = () => {
  const [current, setCurrent] = useState('mail');
  const navigate = useNavigate();

  const handleClick = (e) => {
    console.log('click ', e);
    setCurrent(e.key);
  };

  return (
    <div >
      <Menu onClick={handleClick} selectedKeys={[current]} mode="horizontal" style={{ display: 'flex', justifyContent: 'flex-end' }}>
        {items.map(item => {
          if (item.children) {
            return (
              <SubMenu key={item.key} title={item.avatar} icon={item.icon}>
                {item.children.map(child => (
                  <Menu.Item key={child.key}>{child.label}</Menu.Item>
                ))}
              </SubMenu>
            );
          } else {
            return (
              <Menu.Item key={item.key} icon={item.icon}>
                {item.avatar}
              </Menu.Item>
            );
          }
        })}
      </Menu>
      <h2>Home Page</h2>
      <p>Welcome to the Home Page!</p>

    </div>
  );
};

export default Home;
