import React, { useState, useRef, useEffect } from 'react';
import { Form, Input, Button, Checkbox, Row, Col, ConfigProvider, Modal, message,Timeline } from 'antd';
import "./contact.css";
import axios from 'axios';
const { TextArea } = Input;

function AppContact({ userData, apiUrl }) {
  const [isModalVisible, setModalVisible] = useState(false);
  const formRef = useRef(null);
  const [tasks, setTasks] = useState([]);
  const sortedTasks = tasks.sort((a, b) => {
    // Assuming date is in ISO format, you can parse and compare as needed
    return new Date(a.date) - new Date(b.date);
  });

  const openModal = () => {
    setModalVisible(true);
    formRef.current.resetFields();
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  const onFinish = (values) => {
    console.log('Received values:', values);
    openModal();
  };

  const fetchDataAndFilterTasks = async () => {
    try {
      const response = await axios.get(apiUrl);
      const allData = response.data.data;
      const filteredUsers = allData.filter(item => item.UserID === userData.UserID);
      const tasksWithSubtasks = filteredUsers.map(user => ({
        task: user.Task,
        date: user.Date,
        subtasks: user.SubTask ? user.SubTask.split(',').map(subtask => subtask.trim()) : []
      }));
      setTasks(tasksWithSubtasks);
    } catch (error) {
      console.error('Failed to fetch data', error);
      message.error('Veriler alınamadı. Lütfen daha sonra tekrar deneyin.');
    }
  };

  useEffect(() => {
    fetchDataAndFilterTasks();
  }, []);

  return (
    <ConfigProvider
      theme={{
        token: {
          colorPrimary: '#d11ba7',
          colorPrimaryHover: '#5b10b5',
          colorText: "#d11ba7",
        },
      }}
    >
      <div id="contact" className="contact">
        <div className="contentHolder">
          <div>
            <div className="titleHolder">
              <h2>{userData.User} Görevleri</h2>
            </div>
            <Form
              name="contact_form"
              className="contact-form"
              initialValues={{
                remember: false,
                fullname: userData.User,
                email: userData.Email,
              }}
              onFinish={onFinish}
              ref={formRef}
            >
              <Row gutter={[16, 16]}>
                <Col span={12}>
                  <Form.Item
                    name="fullname"
                    rules={[{ required: true, message: 'Please enter your full name!' }]}
                  >
                    <Input placeholder="Full Name" />
                  </Form.Item>
                </Col>
                <Col span={12}>
                  <Form.Item
                    name="email"
                    rules={[
                      {
                        type: 'email',
                        message: 'Please enter a valid E-mail address!',
                      },
                      {
                        required: true,
                        message: 'Please input E-mail address!',
                      },
                    ]}
                  >
                    <Input placeholder="Email Address" />
                  </Form.Item>
                </Col>
              </Row>
              <Timeline mode="alternate">
                {tasks.map((taskObj, index) => (
                  <Timeline.Item key={index}>
                    <p style={{ fontWeight: "bold", fontSize: "18px" }}>{taskObj.task}</p>
                    <p>{taskObj.date}</p>
                    <ul>
                      {taskObj.subtasks.map((subtask, subIndex) => (
                        <li key={subIndex} style={{ fontWeight: "normal", fontSize: "14px" }}>{subtask}</li>
                      ))}
                    </ul>
                  </Timeline.Item>
                ))}
              </Timeline>

              <Form.Item
                name="remember"
                valuePropName="checked"
                noStyle
                rules={[
                  { validator: (_, value) => value ? Promise.resolve() : Promise.reject('Yollamak için kabul etmelisiniz') },
                ]}
              >
                <Checkbox style={{ color: '#5b10b5' }}>Gönderdiğim verilerin doğruluğunu kabul ediyorum.</Checkbox>
              </Form.Item>
              <Form.Item>
                <Button type='primary' htmlType="submit" style={{ marginTop: '10px' }}>
                  Mail Gönder
                </Button>
              </Form.Item>
            </Form>
            <Modal
              centered
              visible={isModalVisible}
              onCancel={closeModal}
              cancelText="Close"
              closable={false}
              cancelButtonProps={{ type: 'primary' }}
              okButtonProps={{ style: { display: 'none' } }}
              wrapClassName="backdrop-backdrop-filter"
            >
              {isModalVisible && <h2 style={{ textAlign: "center", color: "white" }}>Sent Successfully!</h2>}
            </Modal>
          </div>
        </div>
      </div>
    </ConfigProvider>
  );
}

export default AppContact;
