import React, { useState } from 'react';
import { Menu, Avatar } from 'antd';
import { UserOutlined, MailOutlined, PoweroffOutlined, SettingOutlined, FileTextOutlined, CalendarOutlined } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';

const { SubMenu } = Menu;

const items = [
  {
    label: "Görevler",
    key: 'task',
    icon: <FileTextOutlined />
  },
  {
    label: "Takvim",
    key: 'calendar',
    icon: <CalendarOutlined />
  },
  {
    key: 'userSettings', // Örnek olarak benzersiz bir 'key' tanımlandı
    avatar: <Avatar size={40} icon={<UserOutlined />} />,
    children: [
      {
        label: 'Profil Düzenle',
        key: 'setting:1',
        icon: <SettingOutlined />,
      },
      {
        label: 'Mail yolla',
        key: 'setting:2',
        icon: <MailOutlined />,
      },
    ],
  },
];

const Home = () => {
  const [current, setCurrent] = useState('task'); // Varsayılan seçili 'key' değeri
  const navigate = useNavigate();

  const handleClick = (e) => {
    console.log('click ', e);
    setCurrent(e.key);
  };

  const handleLogout = () => {
    navigate(-1); // Çıkış işlemi veya önceki sayfaya gitme işlemi
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
      <Menu onClick={handleClick} selectedKeys={[current]} mode="horizontal">
        {items.map(item =>
          item.children ? (
            <SubMenu key={item.key} title={item.avatar} icon={item.icon}>
              {item.children.map(child => (
                <Menu.Item key={child.key} icon={child.icon}>
                  {child.label}
                </Menu.Item>
              ))}
              <Menu.Item key="logout" icon={<PoweroffOutlined />} onClick={handleLogout}>
                Çıkış
              </Menu.Item>
            </SubMenu>
          ) : (
            <Menu.Item key={item.key} icon={item.icon}>
              {item.label}
            </Menu.Item>
          )
        )}
      </Menu>
      <div style={{ margin: '20px', textAlign: 'center' }}>
        <h2>Anasayfa</h2>
        <p>Anasayfaya hoş geldiniz!</p>
      </div>
    </div>
  );
};

export default Home;
