import React, { useState } from 'react';
import { Drawer, Button, Space } from 'antd';
import { PoweroffOutlined } from '@ant-design/icons';

const Draw = (props) => {
  const [open, setOpen] = useState(true); // Default to open state
  const [placement, setPlacement] = useState('left'); // Default placement 'left'

  const onChange = (e) => {
    setPlacement(e.target.value);
  };

  const handleCloseDrawer = () => {
    setOpen(false);
  };

  return (
    <>
      <Drawer
        title="Basic Drawer"
        placement={placement}
        closable={false}
        onClose={handleCloseDrawer}
        visible={open} // Controlled by open state
        key={placement}
      >
        <Button key="logout" icon={<PoweroffOutlined />} onClick={handleCloseDrawer}>
          Çıkış (Logout)
        </Button>

        <Space direction="vertical">
          {props.itemsList.map((item) => (
            <React.Fragment key={item.key}>
              {item.icon}
              {item.label}
            </React.Fragment>
          ))}
        </Space>
      </Drawer>
    </>
  );
};

export default Draw;
