import { useRef, useState, useEffect } from 'react';
import { message } from 'antd';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginData = () => {
    const navigate = useNavigate();
    const formRef = useRef(null);
    const [data, setData] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const apiUrl = 'https://v1.nocodeapi.com/gizem11/google_sheets/ICsbCoIOoLbnWrHj?tabId=login';

    const handleLogin = async (values) => {
        const { Email, myPassword } = values;

        // Check if the user exists in the fetched data
        const user = data.find(item => item.Email === Email && item.Password === myPassword);

        if (user) {
            message.success('Successful login'); // Başarılı giriş mesajı
             // Form alanlarını sıfırla
            setIsLoading(true); // Yükleniyor durumunu true yap

            // Simüle edilmiş bir yönlendirme (2 saniye sonra)
            setTimeout(() => {
                setIsLoading(false); // Yükleniyor durumunu false yap
                navigate('/home'); // /home sayfasına yönlendirme yap
            }, 1000); // 2000 milisaniye = 2 saniye
            setTimeout(() => {

                message.destroy(); // Destroy current message
            }, 800);
            setTimeout(()=>{
                formRef.current.resetFields();
            },1000)
        } else {
            message.error('Incorrect email or password'); // Hatalı email veya şifre mesajı
        }
    };

    const getData = async () => {
        try {
            const response = await axios.get(apiUrl);
            setData(response.data.data);
        } catch (error) {
            console.error('Failed to fetch data', error);
        }
    };

    useEffect(() => {
        getData(); // Verileri getirme işlemi
    }, []);

    return { formRef, handleLogin, isLoading };
};

export default LoginDa
