import React, { useState } from 'react';
import { Form, Input, Button, message } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';

const TaskEdit = () => {
  const [taskInput, setTaskInput] = useState('');

  const handleTaskInputChange = (e) => {
    setTaskInput(e.target.value);
  };

  const handleAddTask = async () => {
    try {
      const apiUrl = ''; // API endpointinizi buraya ekleyin

      const requestData = {
        task: taskInput,
      };

      const response = await axios.post(apiUrl, requestData);

      message.success('Görev başarıyla eklendi');

      setTaskInput('');

    } catch (error) {
      console.error('Görev eklenirken bir hata oluştu:', error);
      message.error('Görev eklenirken bir hata oluştu');
    }
  };

  return (
    <div>
      <Form onFinish={handleAddTask}>
        <Form.Item name="taskInput">
          <Input
            placeholder="Görev Ekleyin"
            value={taskInput}
            onChange={handleTaskInputChange}
            size="large"
          />
        </Form.Item>
        <Form.Item>
          <Button htmlType="submit" type="primary" icon={<FileAddOutlined />} size="large">
            Ekle
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default TaskEdit;
