import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TaskData = () => {
    const [data, setData] = useState([]);
    const apiUrl = 'https://v1.nocodeapi.com/gizem11/google_sheets/ICsbCoIOoLbnWrHj?tabId=login';

    // Verileri API'den çekme işlemi
    const getData = async () => {
        try {
            const response = await axios.get(apiUrl);
            setData(response.data.data);
        } catch (error) {
            console.error('Failed to fetch data', error);
        }
    };

    useEffect(() => {
        getData(); // Component yüklendiğinde verileri getir
    }, []);

    // Tüm görevleri ekrana gösterme işlemi
    const showAllTasks = () => {
        return (
            <div>
                <h2>All Tasks</h2>
                <ul>
                    {data.map((item, index) => (
                        <li key={index}>
                            <strong>Email:</strong> {item.Email} <br />
                            <strong>User:</strong> {item.User} <br />
                            <strong>Task:</strong> {item.Task}. <br />
                        </li>
                    ))}
                </ul>
            </div>
        );
    };

    return (
        <div>
            {showAllTasks()} {/* Tüm görevleri ekrana gösteren fonksiyonu çağır */}
        </div>
    );
};

export default TaskData;
