import React, { useState, useRef,useEffect } from 'react';
import { Form, Input, Button, message } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';
import Task from '../components/task';

const TaskForm = ({ userData }) => {
    const formRef = useRef(null);
    const [name, setName] = useState('');
    const [data, setData] = useState([]);
    const [filteredTasks, setFilteredTasks] = useState([]);
    const apiUrl = 'https://v1.nocodeapi.com/gizem002/google_sheets/iaBQTHRWCbIvMRlI?tabId=login';

    const fetchDataAndFilterTasks = async () => {
        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;
            setData(allData);

            const filteredUsers = allData.filter(item => item.UserID === userData.UserID);

            if (filteredUsers.length > 0) {
                const tasks = filteredUsers.flatMap(user => user.Task.split(',').map(task => task.trim()));
                setFilteredTasks(tasks);
            } else {
                setFilteredTasks([]);
            }

        } catch (error) {
            console.error('Failed to fetch data', error);
        }
    };

    useEffect(() => {
        // İlk kez çalıştığında ve userData.UserID değiştiğinde veriyi alır
        fetchDataAndFilterTasks();

        // Her 10 saniyede bir veriyi güncellemek için setInterval kullanımı
        const interval = setInterval(() => {
            fetchDataAndFilterTasks();
        }, 10000); // 10 saniyede bir yenileme yapılacak

        // Component unmount olduğunda clearInterval ile interval'i temizle
        return () => clearInterval(interval);
    }, [userData.UserID]); // useEffect dependencies


    const handleChange = (e) => {
        setName(e.target.value);
    };

    const handleSubmit = async () => {
        const data = [
            [
                userData.Email,
                userData.Password,
                userData.User,
                name,
                userData.UserID
            ]
        ];

        try {
            const response = await axios.post(
                apiUrl,
                data,
                {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                }
            );

            formRef.current.resetFields();
            message.success("Başarıyla Eklendi");
            console.log('Görev başarıyla eklendi:', response.data);

             // Yeni görev ekledikten sonra verileri yeniden getir ve filtrele
        } catch (error) {
            message.warning("Ekleme Yapılamadı");
            if (error.response) {
                console.error('Görev eklenirken hata oluştu:', error.response.data);
            } else {
                console.error('Görev eklenirken hata oluştu:', error.message);
            }
        }
    };

    return (
        <div>
            <Form onFinish={handleSubmit} ref={formRef}>
                <Form.Item
                    name="taskInput"
                >
                    <Input
                        name='görev'
                        placeholder="Görev Ekleyin"
                        value={name}
                        onChange={handleChange}
                        size="large"
                    />
                </Form.Item>
                <Form.Item>
                    <Button
                        htmlType="submit"
                        type="primary"
                        icon={<FileAddOutlined />}
                        size="large"
                    >
                        Ekle
                    </Button>
                </Form.Item>
            </Form>
            <Task userData={userData}></Task>


        </div>
    );
};

export default TaskForm;
