import React, { useState } from 'react';
import { Typography, Form, Input, Button, message, ConfigProvider } from 'antd';
import { EyeInvisibleOutlined, EyeOutlined } from '@ant-design/icons'; // Import icons
import "./login.css";

const Login = () => {
    const [showPassword, setShowPassword] = useState(false);

    const handleLogin = (values) => {
        message.success("Başarılı");
    }

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    }

    return (
        <ConfigProvider
            theme={{
                token: {
                    colorPrimary: '#5b10b5',
                    colorPrimaryHover: '#d11ba7',
                    colorText: "#fff",
                },
            }}
        >
            <div className="appBg">
                <Form className="loginForm" onFinish={handleLogin}>
                    <Typography.Title style={{ textAlign: 'center' }}>Hoş Geldiniz</Typography.Title>

                    <Form.Item
                        name="myEmail"
                        rules={[
                            {
                                required: true,
                                type: "email",
                                message: "Lütfen geçerli bir email adresi girin."
                            }
                        ]}
                    >
                        <Input style={{ width: '100%', color: "black", borderRadius: '20px', border: '1px solid rgba(108, 51, 179, 0.5)', padding: '18px 26px' }} placeholder="Email" />
                    </Form.Item>

                    <Form.Item
                        name="myPassword"
                        rules={[
                            {
                                required: true,
                                message: "Lütfen şifrenizi girin."
                            }
                        ]}
                    >
                        <Input.Password
                            placeholder="Şifre"
                            iconRender={visible => (visible ? <EyeOutlined /> : <EyeInvisibleOutlined />)}
                            style={{ width: '100%', color: "black", borderRadius: '20px', border: '1px solid rgba(108, 51, 179, 0.5)', padding: '18px 26px' }}
                        />
                    </Form.Item>

                    <Form.Item style={{ textAlign: 'center' }}>
                        <Button type='primary' htmlType='submit' block size='large'>Giriş Yap</Button>
                    </Form.Item>
                </Form>
            </div>
        </ConfigProvider>
    );
}

export default Login;
