import React, { useState } from 'react';
import { Form, Input, Button } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';

const TaskForm = ({ userData }) => {
    const [name, setName] = useState('');
    const [userExists, setUserExists] = useState(true); // Kullanıcı var mı kontrolü için state

    const handleChange = (e) => {
        setName(e.target.value);
    };

    const handleSubmit = async () => {
        // Öncelikle kullanıcıyı kontrol edin
        try {
            const userResponse = await axios.get(
                'https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm?tabId=users',
                {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                }
            );
            const users = userResponse.data;
            const userExists = users.some(user => user.UserID === userData.UserID);

            if (!userExists) {
                setUserExists(false);
                console.error('Kullanıcı mevcut değil!');
                return;
            }

            // Kullanıcı mevcutsa görevi ekleyin
            const data = [
                [
                    userData.Email,
                    userData.Password,
                    userData.User,
                    name,
                    userData.UserID
                ]
            ];

            const taskResponse = await axios.post(
                'https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm?tabId=tasks',
                { data },
                {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                }
            );
            console.log('Görev başarıyla eklendi:', taskResponse.data);
        } catch (error) {
            if (error.response) {
                console.error('Hata oluştu:', error.response.data);
            } else {
                console.error('Hata oluştu:', error.message);
            }
        }
    };

    return (
        <div>
            <Form onFinish={handleSubmit}>
                <Form.Item
                    name="taskInput"
                    rules={[{ required: true, message: 'Lütfen bir görev girin!' }]}
                >
                    <Input
                        name='görev'
                        placeholder="Görev Ekleyin"
                        value={name}
                        onChange={handleChange}
                        size="large"
                    />
                </Form.Item>
                {!userExists && <div style={{ color: 'red' }}>Kullanıcı mevcut değil!</div>}
                <Form.Item>
                    <Button
                        htmlType="submit"
                        type="primary"
                        icon={<FileAddOutlined />}
                        size="large"
                        disabled={!userExists} // Kullanıcı mevcut değilse butonu devre dışı bırakın
                    >
                        Ekle
                    </Button>
                </Form.Item>
            </Form>
        </div>
    );
};

export default TaskForm;
