import React, { useState } from 'react';
import { Drawer, Button, Space, Avatar, ConfigProvider } from 'antd';
import { PoweroffOutlined, UserOutlined } from '@ant-design/icons';
import DefaultPage from '../views/defaultPage';
import { useNavigate } from 'react-router-dom';
import AppContact from '../components/contact';
import TaskList from './tasklist';
import TaskAdd from './taskAdd';
import TaskAddList from './taskAddList';

const Draw = ({ userData, itemsList }) => {
  const [selectedItem, setSelectedItem] = useState(null);

  const handleItemClick = (item) => {
    setSelectedItem(item.key);
  };

  const navigate = useNavigate();

  const onLogout = () => {
    navigate(-1); // Çıkış yap veya bir önceki sayfaya geri dön
  };

  // Seçilen öğeye göre hangi bileşeni göstereceğimizi belirleyen fonksiyon
  const renderComponent = () => {
    switch (selectedItem) {
      case 'task':
        return <TaskList userData={userData}></TaskList>
      case 'taskEdit':
        return <TaskAdd userData={userData} />;
      case 'taskDelete':
        return <TaskAddList userData={userData} />;
      case 'mail':
        return <AppContact></AppContact>
      default:
        return <DefaultPage userData={userData} />;
    }
  };

  return (
    <ConfigProvider
      theme={{
        token: {
          colorBgElevated: "#d11ba7"
        }
      }}
    >
      <Drawer
        title={
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <Avatar size="large" icon={<UserOutlined />} />
            <span style={{ marginLeft: '10px', fontSize: '20px', fontWeight: 'bold' }}>
              {userData.User}
            </span>
          </div>
        }
        placement="left"
        mask={false}
        closable={false}
        onClose={() => { }} // Kapatma işlemi olmadığı için boş fonksiyon
        visible={true} // Drawer'ı her zaman açık tut
      >
        <Space direction="vertical" style={{ width: '100%' }} align='start'>
          {itemsList.map((item) => (
            <Button
              key={item.key}
              type="text"
              icon={item.icon}
              style={{
                width: '100%',
                textAlign: 'left',
                fontSize: '16px',
              }}
              onClick={() => handleItemClick(item)}
            >
              {item.label}
            </Button>
          ))}
          <Button
            key="logout"
            type="text"
            icon={<PoweroffOutlined />}
            style={{ width: '100%', textAlign: 'left', fontSize: '16px' }}
            onClick={onLogout}
          >
            Çıkış
          </Button>
        </Space>
      </Drawer>
      <div style={{ marginLeft: '400px', padding: '20px' }}>
        {renderComponent()}
      </div>
    </ConfigProvider>
  );
};

export default Draw;
