import React, { useState, useEffect } from 'react';
import { ConfigProvider, message, Timeline } from 'antd';
import axios from 'axios';

const TimeLine = ({ userData, apiUrl }) => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchDataAndFilterTasks = async () => {
      try {
        const response = await axios.get(apiUrl);
        const allData = response.data.data;

        // Filtrelenmiş ve tarih sıralaması yapılmış görevleri al
        const filteredTasks = allData.filter(item => item.UserID === userData.UserID)
                                     .map(task => ({
                                       task: task.Task,
                                       date: task.Date,
                                       subtasks: task.SubTask ? task.SubTask.split(',').map(subtask => subtask.trim()) : [],
                                       subdates: task.SubTaskDateRanges ? task.SubTaskDateRanges.split(',').map(subdate => subdate.trim()) : []
                                     }))
                                     .sort((a, b) => new Date(a.date) - new Date(b.date));

        setTasks(filteredTasks);
      } catch (error) {
        console.error('Failed to fetch data', error);
        message.error('Veriler alınamadı. Lütfen daha sonra tekrar deneyin.');
      }
    };

    fetchDataAndFilterTasks();
  }, [apiUrl, userData.UserID]);

  return (
    <ConfigProvider
      theme={{
        token: {
          colorPrimary: '#d11ba7',
          colorPrimaryHover: '#5b10b5',
          colorText: "#d11ba7",
        },
      }}
    >
      <div id="contact" className="contact">
        <div className="contentHolder">
          <div>
            <div className="titleHolder">
              <h2>{userData.User} Görev Zaman Çizelgesi</h2>
            </div>
            <div style={{ maxHeight: '700px', overflowY: 'auto' }}>
              <Timeline mode="alternate">
                {tasks.map((taskObj, index) => (
                  <Timeline.Item key={index} label={taskObj.date}>
                    <p style={{ fontWeight: "bold", fontSize: "18px" }}>{taskObj.task}</p>
                    <ul style={{ listStyleType: 'none', padding: 0 }}>
                      {taskObj.subtasks.map((subtask, subIndex) => (
                        <li key={subIndex} style={{ display: 'flex', gap: '20px', fontWeight: 'normal', fontSize: '14px' }}>
                          {subtask} {taskObj.subdates[subIndex]}
                        </li>
                      ))}
                    </ul>
                  </Timeline.Item>
                ))}
              </Timeline>
            </div>
          </div>
        </div>
      </div>
    </ConfigProvider>
  );
};

export default TimeLine;
