import React from "react";
import { Card, Descriptions, Avatar, ConfigProvider } from 'antd';

const Profile = ({ userData }) => {
    return (
        <ConfigProvider
            theme={{
                token: {
                    colorBgContainer: 'linear-gradient(90.21deg, #fc40b1 -5.91%, #4A2FBD 111.58%)',
                    colorBorderSecondary: 'rgba(89, 4, 163, 0.945)',
                    colorText: 'black',
                    colorTextDescription: 'black',
                },
            }}
        >
            <Card
                title="Kullanıcı Profili"
                bordered={true}
                style={{
                    width: 450,
                    margin: '50px auto',
                    borderRadius: '10px',
                    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                }}
                headStyle={{
                    backgroundColor: 'rgba(89, 4, 163, 0.945)',
                    color: 'white',
                    textAlign: 'center',
                    borderRadius: '10px 10px 0 0',
                }}
                bodyStyle={{
                    padding: '20px',
                    color:'white',
                    borderRadius: '0 0 10px 10px',
                }}
            >
                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '30px' }}>
                    <Avatar
                        size={158}
                        src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSFq_XICspIbMdmG2muTxEwmrZfmtUn0TDdT1rq3CP-MsHNyuBDCMhSnV9Phd8QnGNr9Uo&usqp=CAU"
                        style={{
                            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
                        }}
                    />
                </div>
                <Descriptions column={1} bordered style={{ borderColor: 'rgba(89, 4, 163, 0.945)' }}>
                    <Descriptions.Item label="İsim" style={{ fontWeight: 'bold' }}>{userData.User}</Descriptions.Item>
                    <Descriptions.Item label="E-Posta" style={{ fontWeight: 'bold' }}>{userData.Email}</Descriptions.Item>
                    <Descriptions.Item label="Telefon" style={{ fontWeight: 'bold' }}>{userData.Telephone}</Descriptions.Item>
                    <Descriptions.Item label="Adres" style={{ fontWeight: 'bold' }}>{userData.Address}</Descriptions.Item>
                </Descriptions>
            </Card>
        </ConfigProvider>
    );
};

export default Profile;
