import React, { useState } from 'react';
import { Menu, Avatar } from 'antd';
import { UserOutlined, MailOutlined, PoweroffOutlined, SettingOutlined, FileTextOutlined, CalendarOutlined } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';

const { SubMenu } = Menu;

const items = [
  {
    label: "Görevler",
    key: 'task',
    icon: <FileTextOutlined />
  },
  {
    label: "Takvim",
    key: 'calendar',
    icon: <CalendarOutlined />
  },
  {
    label: 'Mail',
    key: 'mail',
    icon: <MailOutlined />,
  },
  {
    key: 'app',
    avatar: <Avatar size={35} icon={<UserOutlined />} />,
    children: [
      {
        label: 'Profil Düzenle',
        key: 'setting:1',
        icon: <SettingOutlined />,
      },

    ],
  },
];

const Header = () => {
  const [current, setCurrent] = useState(''); // Set a default selected key
  const navigate = useNavigate();

  const handleClick = (e) => {
    console.log('click ', e);
    setCurrent(e.key);
  };

  const handleLogout = () => {
    navigate(-1); // Navigate to previous page or perform logout action
  };

  return (
      <div >
        <Menu onClick={handleClick} selectedKeys={[current]} mode="horizontal" style={{ display: 'flex', justifyContent: 'flex-end'}}>
          {items.map(item =>
            item.children ? (
              <SubMenu key={item.key} title={item.avatar} icon={item.icon}>
                {item.children.map(child => (
                  <Menu.Item key={child.key} icon={child.icon}>
                    {child.label}
                  </Menu.Item>
                ))}
                <Menu.Item key="logout" icon={<PoweroffOutlined />} onClick={handleLogout}>
                  Çıkış
                </Menu.Item>
              </SubMenu>
            ) : (
              <Menu.Item key={item.key} icon={item.icon}>
                {item.label}
              </Menu.Item>
            )
          )}
        </Menu>
      </div>

  );
};

export default Header;
