import React from 'react';
import { Typography, Form, Input, Button, message, ConfigProvider } from 'antd';
import "./login.css";
import { UserOutlined } from '@ant-design/icons';
const Login = () => {

    const handleLogin = (values) => {
        message.success("Başarılı");

    }

    return (
        <ConfigProvider
            theme={{
                token: {
                    colorPrimary: '#5b10b5',
                    colorText:"#fff",
                    colorPrimaryHover: '#d11ba7', // Örnek bir renk tokenı
                    colorBgContainer:"rgba(108, 51, 179, 0.1)",
                    colorIcon:"black",
                    colorIconHover:"black"
                    //colorBgMask:"#d11ba7"
                },


            }}
        >
            <div className="appBg">
                <Form className="loginForm" onFinish={handleLogin}>
                    <Typography.Title style={{ textAlign: 'center' }}>Hoş Geldiniz</Typography.Title>

                    <Form.Item

                        name="myEmail"
                        rules={[
                            {
                                required: true,
                                type: "email",
                                message: "Lütfen geçerli bir email adresi girin."
                            }
                        ]}
                    >
                        <Input placeholder="Email" suffix={<UserOutlined />} size='large' style={{color:""}} />
                    </Form.Item>
                    <Form.Item

                        name="myPassword"
                        rules={[
                            {
                                required: true,
                                message: "Lütfen şifrenizi girin."
                            }
                        ]}
                    >
                        <Input.Password placeholder="Şifre" size='large' />
                    </Form.Item>
                    <Form.Item style={{ textAlign: 'center' }}>
                        <Button type='primary' htmlType='submit'  block size='large'>Giriş Yap</Button>
                    </Form.Item>


                </Form>
            </div>
        </ConfigProvider>
    );
}

export default Login;
