import React, { useState, useEffect } from 'react';
import { Button } from 'antd';
import TaskData from '../services/taskdata';

const Task = ({ userData }) => {
    const { filteredTasks, refetchDataAndFilter } = TaskData({ userData });

    useEffect(() => {
        // Her filteredTasks değiştiğinde çalışacak
        console.log('filteredTasks güncellendi:', filteredTasks);
    }, [filteredTasks]);

    return (
        <div style={{ padding: '20px' }}>
            <h2>Görevleriniz:</h2>
            {filteredTasks.length > 0 ? (
                filteredTasks.map((task, index) => (
                    <Button key={index} style={{ marginBottom: '10px' }} block>
                        {task}
                    </Button>
                ))
            ) : (
                "Yükleniyor..."
            )}
        </div>
    );
};

export default Task;
