import React, { useState } from 'react';
import { Drawer, Button, Space, Avatar } from 'antd';
import { PoweroffOutlined, UserOutlined } from '@ant-design/icons';
import { MailOutlined, SettingOutlined, FileTextOutlined, CalendarOutlined, FileAddOutlined } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import Task from '../components/task';



const Draw = ({ userData,itemsList}) => {
  const navigate = useNavigate();
  const [selectedMenuItem, setSelectedMenuItem] = useState(null);

  const onLogout = () => {
    navigate(-1); // Navigate to previous page or perform logout action
  };

  const onItemClick = (key) => {
    setSelectedMenuItem(key);
  };

  return (
    <Drawer
      title={
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <Avatar size="large" icon={<UserOutlined />} />
          <span style={{ marginLeft: '10px', fontSize: '20px', fontWeight: 'bold' }}>
            {userData.User}
          </span>
        </div>
      }
      placement="left"
      closable={false}
      mask={false}
      onClose={() => { }}
      visible={true}
    >
      <Space direction="vertical" align="start" style={{ width: '100%' }}>
        {itemsList.map((item) => (
          <Button
            key={item.key}
            type="text"
            icon={item.icon}
            style={{ width: '100%', textAlign: 'left', fontSize: '16px' }}
            onClick={() => onItemClick(item.key)}
          >
            {item.label}
          </Button>
        ))}

        <Button
          key="logout"
          type="primary"
          icon={<PoweroffOutlined />}
          style={{ width: '100%', marginLeft:'16px', fontSize: '18px' }}
          onClick={onLogout}
        >
          Çıkış
        </Button>
      </Space>


        {selectedMenuItem === 'task' && <Task userData={userData} />}

    </Drawer>
  );
};

export default Draw;
