import React from 'react';
import { useLocation } from 'react-router-dom';
import Draw from "/Users/gizem/Desktop/reactproje/src/common/drawer.js"
import Task from '../components/task';

import { MailOutlined, SettingOutlined, FileTextOutlined, CalendarOutlined, FileAddOutlined } from '@ant-design/icons';


const itemsList = [
  {
    label: "Görevler",
    key: 'task',
    icon: <FileTextOutlined />,
    route: '/tasks', // Örnek bir route
  },
  {
    label: "Görev Ekle",
    key: 'task2',
    icon: <FileAddOutlined />,
    route: '/add-task', // Örnek bir route
  },
  {
    label: "Takvim",
    key: 'calendar',
    icon: <CalendarOutlined />,
    route: '/calendar', // Örnek bir route
  },
  {
    label: 'Mail',
    key: 'mail',
    icon: <MailOutlined />,
    route: '/mail', // Örnek bir route
  },
  {
    label: 'Profil Düzenle',
    key: 'setting:1',
    icon: <SettingOutlined />,
    route: '/edit-profile', // Örnek bir route
  }
];


const Home = () => {
    const { state } = useLocation();
    const userData = state?.userData;

    return (
        <div>

            <Draw userData={userData} itemsList={itemsList}></Draw>

            <div style={{ marginLeft: '400px', padding: '20px' }}> {/* Drawer genişliği kadar sol taraftan içeri kaydır */}
                <h1>Hoşgeldiniz {userData.User}</h1>


            </div>
        </div>
    );
};

export default Home;
