import React, { useState } from 'react';
import { Form, Input, Button } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';
import Task from '../components/task';

const TaskForm = ({ userData }) => {
  const [name, setName] = useState('');
  const [refreshTasks, setRefreshTasks] = useState(false); // State for triggering Task component refresh
  const apiUrl = 'https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm?tabId=login';

  const handleChange = (e) => {
    setName(e.target.value);
  };

  const handleSubmit = async () => {
    const data = [
      [
        userData.Email,
        userData.Password,
        userData.User,
        name,
        userData.UserID
      ]
    ];

    try {
      const response = await axios.post(
        apiUrl,
        data,
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );
      console.log('Görev başarıyla eklendi:', response.data);
      setName(''); // Clear input field after successful submission
      setRefreshTasks(prev => !prev); // Toggle refresh state to trigger Task component update
    } catch (error) {
      if (error.response) {
        // Sunucudan gelen yanıt var
        console.error('Görev eklenirken hata oluştu:', error.response.data);
      } else {
        // Yanıt yok, istek hiç yapılmadı ya da yapılamadı
        console.error('Görev eklenirken hata oluştu:', error.message);
      }
    }
  };

  return (
    <div>
      <Form onFinish={handleSubmit}>
        <Form.Item
          name="taskInput"
          rules={[{ required: true, message: 'Lütfen bir görev girin!' }]}
        >
          <Input
            name='görev'
            placeholder="Görev Ekleyin"
            value={name}
            onChange={handleChange}
            size="large"
          />
        </Form.Item>
        <Form.Item>
          <Button
            htmlType="submit"
            type="primary"
            icon={<FileAddOutlined />}
            size="large"
          >
            Ekle
          </Button>
        </Form.Item>
      </Form>
      <Task userData={userData} key={refreshTasks ? 'refresh' : 'no-refresh'} />
    </div>
  );
};

export default TaskForm;
