import React, { useState } from 'react';
import { Form, Input, Button, message } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';

const TaskEdit = () => {
    const [name, setName] = useState('');

    const handleChange = event => {
        setName(event.target.value);
    }

    const handleSubmit = event => {
        event.preventDefault();

        const user = {
            name: name
        };

        axios.post(`https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm?tabId=login`, { user })
            .then(res => {
                console.log(res);
                console.log(res.data);
                message.success('Görev başarıyla eklendi');
            })
            .catch(error => {
                console.error('Hata oluştu:', error);
                message.error('Görev eklenirken bir hata oluştu');
            });
    }

    return (
        <div>
            <Form onFinish={handleSubmit}>
                <Form.Item
                    name="taskInput"
                    rules={[{ required: true, message: 'Lütfen bir görev girin!' }]}
                >
                    <Input
                        name='görev'
                        placeholder="Görev Ekleyin"
                        value={name}
                        onChange={handleChange}
                        size="large"
                    />
                </Form.Item>
                <Form.Item>
                    <Button
                        htmlType="submit"
                        type="primary"
                        icon={<FileAddOutlined />}
                        size="large"
                    >
                        Ekle
                    </Button>
                </Form.Item>
            </Form>
        </div>
    );
};

export default TaskEdit;
