import React, { useState, useEffect } from 'react';
import { Calendar, Badge, message } from 'antd';
import axios from 'axios';

const TaskCalendar = ({ userData,apiUrl }) => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchDataAndFilterTasks = async () => {
      try {
        const response = await axios.get(apiUrl);
        const allData = response.data.data;
        // Filter tasks based on UserID
        const filteredTasks = allData.filter(item => item.UserID === userData.UserID);
        // Transform tasks into required format
        const tasksWithSubtasks = filteredTasks.map(task => ({
          task: task.Task,
          date: task.Date,
          subtasks: task.SubTask ? task.SubTask.split(',').map(subtask => subtask.trim()) : [],
          subdates: task.SubTaskDateRanges ? task.SubTaskDateRanges.split(',').map(subdate => subdate.trim()) : []
        }));
        co
        setTasks(tasksWithSubtasks);
      } catch (error) {
        console.error('Failed to fetch data', error);
        message.error('Veriler alınamadı. Lütfen daha sonra tekrar deneyin.');
      }
    };

    fetchDataAndFilterTasks();
  }, [apiUrl, userData.UserID]);

  const dateCellRender = (value) => {
    const dateString = value.format('YYYY-MM-DD');
    // Filter tasks for the selected date
    const tasksForDate = tasks.filter(task => task.date === dateString);
    // Prepare subtasks for rendering
    const subtasksForDate = tasksForDate.flatMap(task =>
      task.subtasks.map((subtask, index) => ({
        subtask,
        subdate: task.subdates[index]
      }))
    );

    return (
      <div className="events">
        <ul className="events">
          {tasksForDate.map(task => (
            <li key={task.task}>
              <Badge status="processing" text={task.task} />
            </li>
          ))}
        </ul>
        <ul className="subevents">
          {subtasksForDate.map((subtask, index) => (
            <li key={`${subtask.subtask}-${index}`}>
              <Badge status="success" text={`${subtask.subtask} - ${subtask.subdate}`} />
            </li>
          ))}
        </ul>
      </div>
    );
  };

  return (
    <div style={{ width: '100%', maxWidth: '800px', margin: '0 auto' }}>
      <Calendar dateCellRender={dateCellRender} />
    </div>
  );
};

export default TaskCalendar;
