import React from 'react';
import { Button, message, Popconfirm } from 'antd';
import axios from 'axios';

const TaskList = ({ data }) => {

    const handleUpdate = (task) => {
        console.log('Güncelle:', task);
        // Güncelleme işlemini burada gerçekleştir (örneğin, bir form gösterilebilir)
    };

    const handleDelete = async (task) => {
        console.log('Sil:', task);

        try {
            // Görevi silmek için gerekli işlemleri burada yapabilirsiniz
            // Örneğin, axios ile bir API çağrısı yaparak veritabanından görevi silin

            // Simülasyon amacıyla, görevin sildiği varsayılarak yerel state'i güncelliyoruz
            // Gerçek uygulamada, API çağrısından dönen sonuca göre state güncellenir
            const updatedData = data.filter(item => item !== task);
            message.success('Görev başarıyla silindi.');

            // Veritabanından silme işlemi
            // await axios.delete(`https://example.com/tasks/${task.id}`); // Buradaki URL ve endpoint sizin gerçek API'nize göre değişir

            // setState(updatedData); şeklinde state güncellemesi yapılabilir
        } catch (error) {
            console.error('Görev silinirken hata oluştu:', error);
            message.error('Görev silinirken bir hata oluştu. Lütfen tekrar deneyin.');
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>Görevleriniz:</h2>
            {data && data.length > 0 ? (
                data.map((task, index) => (
                    <div key={index} style={{ marginBottom: '10px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <Button style={{ flex: 1, marginRight: '10px' }} block>
                            {task}
                        </Button>
                        <div>
                            <Button type="primary" style={{ marginRight: '5px' }} onClick={() => handleUpdate(task)}>Güncelle</Button>
                            <Popconfirm
                                title="Bu görevi silmek istediğinizden emin misiniz?"
                                onConfirm={() => handleDelete(task)}
                                okText="Evet"
                                cancelText="Hayır"
                            >
                                <Button type="danger">Sil</Button>
                            </Popconfirm>
                        </div>
                    </div>
                ))
            ) : (
                <p>Görev bulunamadı.</p>
            )}
        </div>
    );
};

export default TaskList;
