import React, { useState, useEffect } from 'react';
import { Button, message } from 'antd';
import axios from 'axios';
import TaskData from '../services/taskdata';

const Task = ({userData}) => {
  const {filteredTasks}=TaskData(userData);

  return (
    <div style={{ padding: '20px' }}>
      <h2>Görevleriniz:</h2>
      {filteredTasks.length > 0 && (
        filteredTasks.map((task, index) => (
          <Button key={index} style={{ marginBottom: '10px'}} block>
            {task}
          </Button>
        ))
      )}

      {filteredTasks.length === 0 && (
        "Yükleniyor..."
      )}
    </div>
  );

};

export default Task;
