import './App.css';

import { MailOutlined, SettingOutlined, FileTextOutlined, CalendarOutlined, FileAddOutlined } from '@ant-design/icons';
import Login from './components/login';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './views/home';
import Task from './components/task';
import TaskForm from './common/taskEdit';


// Define itemsList here
const itemsList = [
  {
    label: "Görevler",
    key: 'task',
    icon: <FileTextOutlined />,
    route: 'task',
  },
  {
    label: "Görev Ekle",
    key: 'taskEdit',
    icon: <FileAddOutlined />,
    route: 'add-task',
  },
  {
    label: "Takvim",
    key: 'calendar',
    icon: <CalendarOutlined />,
    route: 'calendar',
  },
  {
    label: 'Mail',
    key: 'mail',
    icon: <MailOutlined />,
    route: 'mail',
  },
  {
    label: 'Profil Düzenle',
    key: 'setting:1',
    icon: <SettingOutlined />,
    route: 'edit-profile',
  }
];

function App() {
  return (

    <Router>
      <div className="App">
        {/* The Login component will always render as the default route */}
        <Routes>
          <Route path="/" element={<Login />} />
          <Route path="/home" element={<Home itemsList={itemsList} />} />
          <Route path="/task" element={<Task/>} />
          <Route path='/home/add' element={<TaskForm u/>}></Route>
        </Routes>
      </div>
    </Router>


  );
}

export default App;
