import { Button, Popconfirm, message, ConfigProvider, TimePicker } from "antd";
import { FileExcelOutlined, DownOutlined, UpOutlined } from '@ant-design/icons';
import React, { useState } from "react";
import axios from 'axios';

const TaskList = ({ task, deleteTask, userData, apiUrl }) => {
    const [subTask, setSubTask] = useState([]);
    const [showSubTask, setShowSubTask] = useState(false);
    const [selectedSubTask, setSelectedSubTask] = useState(null);
    const [clickedSubtaskIndex, setClickedSubtaskIndex] = useState(null);

  const handleSubtaskClick = (index) => {
    setClickedSubtaskIndex(index);
  };

  const handleAddClick = (index) => {
    // Handle the add button click event
    message.success(`Subtask ${index} time range added!`);
    // Reset the clicked subtask index after adding
    setClickedSubtaskIndex(null);
  };
    const handleButtonClick = (index) => {
        setSelectedSubTask(index);
    };
    const handleDelete = async (task) => {
        console.log('Sil:', task);

        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;
            // Silinecek task'in kimliğini (row_id) al
            const deletedData = allData.find(item => item.Task === task && item.UserID===userData.UserID);
            if (!deletedData) {
                throw new Error('Görev bulunamadı.'); // Eğer görev bulunamazsa hata fırlat
            }

            const row_id = deletedData.row_id; // Görevin kimliği (row_id)

            // Veritabanından silme işlemi için DELETE isteği gönder
            await axios.delete(`${apiUrl}&row_id=${row_id}`);

            // Başarılı bir şekilde silindiğinde bildirim göster
            message.success('Görev başarıyla silindi.');
            deleteTask(deletedData.Task);

        } catch (error) {
            console.error('Görev silinirken hata oluştu:', error);
            message.error('Görev silinirken bir hata oluştu. Lütfen tekrar deneyin.');
        }
    };

    const handleItemClick = async (task) => {
        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;
            const filteredUsers = allData.filter(item => item.UserID === userData.UserID && item.Task === task);
            const subtasks = filteredUsers.flatMap(user => user.SubTask.split(',').map(SubTask => SubTask.trim()));
            setSubTask(subtasks);
            setShowSubTask(true);
        } catch (error) {
            console.error('Alt görevler alınırken hata oluştu:', error);
            message.error('Alt görev bulunamadı!');
        }
    };

    return (

        <ConfigProvider
            theme={{
                token: {
                    colorPrimary: '#d11ba7',
                    colorPrimaryHover: '#5b10b5',
                    colorIcon: 'black'
                }
            }}


        >
            <div style={{ display: "flex", flexDirection: "column" }}>
                <div style={{ display: "flex" }}>
                    <Button
                        style={{ color: "black", marginRight: '10px', width: '637px' }}
                        onClick={() => handleItemClick(task)}
                    >
                        {task} {showSubTask ? <UpOutlined /> : <DownOutlined />}
                    </Button>

                    <Popconfirm
                        title="Bu görevi silmek istediğinizden emin misiniz?"
                        onConfirm={() => handleDelete(task)}
                        okText="Evet"
                        cancelText="Hayır"
                    >
                        <Button
                            icon={<FileExcelOutlined />}
                            type="primary"
                            style={{ marginBlockEnd: "10px" }}
                        >
                            Sil
                        </Button>
                    </Popconfirm>
                </div>

                {showSubTask && (
                    <div style={{ marginLeft: '20px' }}>
                        {subTask.map((subtask, index) => (
                            <Button key={index} style={{ marginBlockEnd: '10px' }} onClick={() => <TimePicker.RangePicker />}>{subtask}</Button>
                        ))}
                    </div>
                )}
            </div>



        </ConfigProvider>

    );
}

export default TaskList;
