import React, { useState, useEffect } from 'react';
import { Button } from 'antd';
import axios from 'axios';
import { FileAddOutlined } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';

const Task = ({ userData }) => {
    const navigate = useNavigate();
    const [data, setData] = useState([]);
    const [filteredTasks, setFilteredTasks] = useState([]);
    const apiUrl = 'https://v1.nocodeapi.com/gizem004/google_sheets/rmLuKVNiJbmqpjdA?tabId=login';

    const getData = async () => {
        try {
            const response = await axios.get(`${apiUrl}`);

            setData(response.data.data);
        } catch (error) {
            console.error('Failed to fetch data', error);
        }
    };

    useEffect(() => {
        getData(); // Fetch data
    }, []);

    // Kullanıcının görevlerini filtrele ve state'e set et
    useEffect(() => {
        const filteredUsers = data.filter(item => item.UserID === userData.UserID);

        if (filteredUsers.length > 0) {
            const tasks = filteredUsers.flatMap(user => user.Task.split(',').map(task => task.trim()));
            setFilteredTasks(tasks);
        } else {
            setFilteredTasks([]);
        }
    }, [data, userData.UserID]); // useEffect'in bağımlılıkları: data ve userData.UserID
    const handleSubmit = async (data) => {

        try {
            const response = await axios.post(apiUrl, data, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            formRef.current.resetFields(); // Formu temizle
            message.success('Başarıyla Eklendi');
            console.log('Görev başarıyla eklendi:', response.data);

        } catch (error) {
            message.error('Görev eklenirken bir hata oluştu. Lütfen tekrar deneyin.');
            console.error('Görev eklenirken hata oluştu:', error);
        }

    };

    return (
        <div >
            <Button
                htmlType="submit"
                type="primary"
                icon={<FileAddOutlined />}
                size="large"
                onClick={()=> navigate('/home/add', { state: { userData: userData }})} // İşlem tamamlandıktan sonra "/home/task" rotasına yönlendir ve verileri gönder}
            >
                Ekle
            </Button>
            <div style={{ padding: '20px' }}>
                <h2>Görevleriniz:</h2>
                {filteredTasks && filteredTasks.length > 0 ? (
                    filteredTasks.map((task, index) => (
                        <Button key={index} style={{ marginBottom: '10px' }} block>
                            {task}
                        </Button>
                    ))
                ) : (
                    "Yükleniyor..."
                )}
            </div>

        </div>
    );
};

export default Task;
