import React, { useState } from 'react';
import { Button, Drawer, Radio, Space } from 'antd';
import { LogoutOutlined, PoweroffOutlined } from '@ant-design/icons'; // Import LogoutOutlined icon

const Draw = ({ itemsList, handleLogout }) => {
  const [open, setOpen] = useState(false); // Default to closed state
  const [placement, setPlacement] = useState('left');

  const showDrawer = () => {
    setOpen(true);
  };

  const hideDrawer = () => {
    setOpen(false);
  };

  const onChange = (e) => {
    setPlacement(e.target.value);
  };

  const renderNestedItems = (items) => {
    return items.map((item) =>
      item.children ? (
        <Space key={item.key} direction="vertical">
          {renderNestedItems(item.children)}
          <Button key="logout" icon={<LogoutOutlined />} onClick={handleLogout}>
            Çıkış (Logout)
          </Button>
        </Space>
      ) : (
        <Space key={item.key} direction="vertical">
          {item.icon && <item.icon />}
          {item.label}
        </Space>
      )
    );
  };

  return (
    <>
      <Button type="primary" onClick={showDrawer}>
        Open Drawer
      </Button>
      <Drawer
        title="Basic Drawer"
        placement={placement}
        closable={true} // Allow closing the drawer
        onClose={hideDrawer} // Function to close the drawer
        visible={open}
        key={placement}
      >
        {renderNestedItems(itemsList)}
      </Drawer>
    </>
  );
};

export default Draw;
